import { count } from 'console';
import { format } from 'path';
import React, {FormEvent, useState} from 'react';
import {User} from "../Types"

type Props = {
    users : User[]
    setUsers : React.Dispatch<React.SetStateAction<User[]>>
    fetchUsers : () => Promise<void>
}

const UserInput: React.FC<Props> = ({users, setUsers, fetchUsers}) => {

  const [inputName, setInputName] = useState("")
  const [inputAge, setInputAge] = useState(0)

  const handleInputChangeName = (e:React.ChangeEvent<HTMLInputElement>) => {
    setInputName(e.target.value)
  }
  const handleInputChangeAge = (e:React.ChangeEvent<HTMLInputElement>) => {
    setInputAge(Number(e.target.value))
  }

  
  const onSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    if (!inputName) {
      alert("Please enter name");
      return;
    }

    if ([...inputName].length > 50) {
      alert("Please enter a name shorter than 50 characters");
      return;
    }

    if (inputAge < 20 || inputAge > 80) {
      alert("Please enter age between 20 and 80");
      return;
    }

    try {
      const result = await fetch("http://localhost:8000/users", {
        method: "POST",
        body: JSON.stringify({
          name: inputName,
          age: inputAge,
        }),
      });
      if (!result.ok) {
        throw Error(`Failed to create user: ${result.status}`);
      }
      setInputName("");
      setInputAge(0);
      fetchUsers();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className = "input-form">
      <form 
        onSubmit={(e) => onSubmit(e)}
        className = "inner">
        <label>Name:</label>
        <input 
          placeholder="Enter User's Name"
          type = "text"
          className ="input"
          value = {inputName}
          onChange={handleInputChangeName}
        />
        <label>Age:</label>
        <input 
          placeholder="Enter User's Age"
          type = "text"
          className ="input"
          value = {inputAge}
          onChange={handleInputChangeAge}
        />
        <button 
           className = "btn is-primary">submit</button>
      </form>
    </div>
  )
}

export default UserInput;
