import {useState} from "react";

const Form = () => {
        const [line, setLine] = useState("");
	const list = [];
	const tweet = (submit, line) => {
		       submit.preventDefault();
		       list.push(line);
		       setLine = ("");
		       console.log(list);
		    }
	return (
                <form style={{ display: "flex", flexDirection: "column" }}>
		          <input
		            type = {"text"}
		            value = {line}
		            onChange = {(e) => setLine(e.target.value)}
		          ></input>
		          <button onClick = {(e) => tweet(e,{line})}>Tweet</button>
	        </form>
	);
}

export default Form;
